import { CartItem } from '../models/cart-item';
import { Cart } from '../models/cart.model';
export class CartUtil {
  static get() {
    // Recupera os dados do LocalStorage
    const data = localStorage.getItem('petshopcart'); // Caso não haja dados, retorna um novo carrinho

    if (!data) return new Cart(); // Caso haja dados, retorna o carrinho

    return JSON.parse(data);
  }

  static add(id, product, quantity, price, image) {
    // Obtém o carrinho
    let cart = this.get(); // Gera o novo item

    const item = new CartItem(id, product, quantity, price, image); // Adiciona ao carrinho

    cart.items.push(item); // Salva no localStorage

    localStorage.setItem('petshopcart', JSON.stringify(cart));
  }

  static update(cart) {
    // Salva no localStorage
    localStorage.setItem('petshopcart', JSON.stringify(cart));
  }

  static clear() {
    localStorage.removeItem('petshopcart');
  }

}
